import { createSlice, createAsyncThunk, PayloadAction } from "@reduxjs/toolkit";
import PizzaService from "../../API/PizzaService.ts";
import { RootState } from "../store.ts";



export type Item = {
  id: string,
  imageUrl: string,
  title: string,
  types: number[],
  sizes: number[],
  price: number,
  category: number,
  rating: number,
}

interface IItem {
  items: Item[],
  status: string
}

const initialState: IItem = {
  items: [],
  status: "loading",
};

export const fetchPizzas = createAsyncThunk(
  "pizza/getAll",
  async (currentPage: number) => {
    const response = await PizzaService.getAll(String(currentPage));

    return response.data as Item[];
  }
);

const pizzaSlice = createSlice({
  name: "pizzas",
  initialState,
  reducers: {
    setItems(state, action) {
      state.items = action.payload;
    },
  },
  extraReducers: (builder) => {
    builder.addCase(fetchPizzas.fulfilled, (state, action: PayloadAction<Item[]>) => {
      state.items = action.payload;
          state.status = "success";
    }),
    builder.addCase(fetchPizzas.rejected, (state) => {
      state.items = [];
          state.status = "error";
    })
    builder.addCase(fetchPizzas.pending, (state) => {
      state.items = [];
          state.status = "loading";
    })
  }

});

export const selectorPizzas = (state: RootState) => state.pizzas;
export const { setItems } = pizzaSlice.actions;

export default pizzaSlice.reducer;
