import React, { useEffect, useState } from "react";
import { PizzaContext } from "../Contexts/PizzaContext.jsx";

export default function Sort() {
  const [isShow, setIsShow] = useState(false);
  const [selectedIndex, setSelectedIndex] = useState(0);
  const { sort, setType, setSort } = React.useContext(PizzaContext);
  useEffect(() => {
    const index = selects.findIndex((select) => select.title === sort.title);
    setSelectedIndex(index);
  }, []);

  const selects = [
    {
      title: "популярности (asc)",
      sort: "rating",
    },
    {
      title: "популярности (desc)",
      sort: "rating",
    },
    {
      title: "цене (asc)",
      sort: "price",
    },
    {
      title: "цене (desc)",
      sort: "price",
    },
    {
      title: "алфавиту (asc)",
      sort: "title",
    },

    {
      title: "алфавиту (desc)",
      sort: "title",
    },
  ];

  function clickSelect(index) {
    if (selects[index].sort === "title") setType("string");
    else setType("number");

    setSort(selects[index].sort);
    setSelectedIndex(index);
    setIsShow(!isShow);
  }

  return (
    <div className="sort">
      <div className="sort__label">
        <svg
          width="10"
          height="6"
          viewBox="0 0 10 6"
          fill="none"
          xmlns="http://www.w3.org/2000/svg"
        >
          <path
            d="M10 5C10 5.16927 9.93815 5.31576 9.81445 5.43945C9.69075 5.56315 9.54427 5.625 9.375 5.625H0.625C0.455729 5.625 0.309245 5.56315 0.185547 5.43945C0.061849 5.31576 0 5.16927 0 5C0 4.83073 0.061849 4.68424 0.185547 4.56055L4.56055 0.185547C4.68424 0.061849 4.83073 0 5 0C5.16927 0 5.31576 0.061849 5.43945 0.185547L9.81445 4.56055C9.93815 4.68424 10 4.83073 10 5Z"
            fill="#2C2C2C"
          />
        </svg>
        <b>Сортировка по:</b>
        <span onClick={() => setIsShow(!isShow)}>
          {selects[selectedIndex].title}
        </span>
      </div>
      <div className={isShow ? "sort__popup visible" : "sort__popup"}>
        <ul>
          {selects.map((select, index) => (
            <li
              key={index}
              onClick={() => clickSelect(index)}
              className={index === selectedIndex ? "active" : ""}
            >
              {select.title}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}
