import React from "react";
import Categories from "../components/Categories.tsx";
import Sort, { selects } from "../components/Sort.tsx";
import { useAxios } from "../hooks/useAxios.ts";

import PizzaCard from "../components/PizzaCard/PizzaCard.tsx";
import PizzaSkeletonCard from "../components/PizzaCard/PizzaSkeletonCard.tsx";
import { PizzaContext } from "../Contexts/PizzaContext";
import { useCategory } from "../hooks/usePizzas.ts";
import Pagination from "../components/Pagination/index.tsx";

import { useSelector, useDispatch } from "react-redux";
import { useNavigate, useSearchParams } from "react-router-dom";
import { selectorFilter, setParams } from "../Redux/slices/filterSlice";
import { fetchPizzas, selectorPizzas } from "../Redux/slices/pizzasSlice";
import qs from "qs";

export default function Home() {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  let [searchParams, setSearchParams] = useSearchParams();
  const { items, status } = useSelector(selectorPizzas);
  const { filter, sort, currentPage, searchValue } =
    useSelector(selectorFilter);
  const [type, setType] = React.useState<String>("number");
  // me
  const [axiosPizzas, isLoadingPizzas, errorPizzas] = useAxios(async (page) => {
    // const response = await PizzaService.getAll(page);

    //@ts-ignore
    console.log(dispatch(fetchPizzas(page)));
    // setPizzas(response.data);
  });
  const sortedArr = useCategory([...items], sort, type, filter, searchValue);

  React.useEffect(() => {
    if (searchParams.size > 0) {
      const sort = selects.find((obj) => obj.sort === searchParams.get("sort"));

      dispatch(
        setParams({
          filter: searchParams.get("filter"),
          currentPage: searchParams.get("currentPage"),
          sort,
        })
      );
    }
  }, []);

  React.useEffect(() => {
    window.scrollTo(0, 0);
    axiosPizzas(currentPage);
  }, [currentPage]);

  React.useEffect(() => {
    const queryString = qs.stringify({
      sort: sort.sort,
      filter,
      currentPage,
    });
    navigate(`?${queryString}`);
  }, [sort.sort, filter, currentPage]);

  return (
    <div className="container">
      <div className="content__top">
        <PizzaContext.Provider value={{ setType, type }}>
          <Categories />
          <Sort setType={setType} />
        </PizzaContext.Provider>
      </div>
      <h2 className="content__title">–í—Å–µ –ø–∏—Ü—Ü—ã</h2>

      {status === "error" ? (
        <h2 style={{ padding: "60px 0", textAlign: "center" }}>
          –ü—Ä–æ–∏–∑–æ—à–ª–∞ –æ—à–∏–±–∫–∞ üò•
        </h2>
      ) : status === "loading" ? (
        <div className="content__items">
          {[...Array(8)].map((_, index) => (
            <PizzaSkeletonCard key={index} />
          ))}{" "}
        </div>
      ) : (
        <div className="content__items">
          {" "}
          {sortedArr.map((item) => (
            <PizzaCard key={item.id} item={item} />
          ))}{" "}
        </div>
      )}

      <Pagination />
    </div>
  );
}
