import { useMemo } from "react";

export function useSort(array, sort, type) {
  const sortedArray = useMemo(() => {
    const sorted = [];
    if (type === "number") {
      sorted = array.sort((objA, objB) => objA[sort] - objB[sort.sort]);
    } else {
      objA[sort].localeCompare(objB[sort.sort]);
    }

    return sorted;
  }, [array, sort]);
  return sortedArray;
}

export function useCategory(array, sort, type, category) {
  const sortedArray = useSort(array, sort, type);
  const filteredArray = useMemo(() => {
    return sortedArray.filter((item) => item.category === category);
  }, [array, category]);
  return filteredArray;
}
