import { useMemo } from "react";

export function useSort(array, sort, type) {
  const sortedArray = useMemo(() => {
    let sorted = [];

    if (type === "number") {
      if (sort.title.includes("(asc)")) {
        sorted = array.sort((objA, objB) => objA[sort.sort] - objB[sort.sort]);
      } else {
        sorted = array.sort((objA, objB) => objB[sort.sort] - objA[sort.sort]);
      }
    } else {
      if (sort.title.includes("(asc)")) {
        sorted = array.sort((objA, objB) =>
          objA[sort.sort].localeCompare(objB[sort.sort])
        );
      } else {
        sorted = array.sort((objA, objB) =>
          objB[sort.sort].localeCompare(objA[sort.sort])
        );
      }
    }

    return sorted;
  }, [array, sort]);
  return sortedArray;
}
export function useSearch(array, searchValue) {
  const searchedArray = useMemo(() => {
    if (searchValue)
      return array.filter((item) =>
        item.title.toLowerCase().includes(searchValue.toLowerCase())
      );
    return array;
  }, [searchValue, array]);
  return searchedArray;
}

export function useCategory(array, sort, type, category, searchValue) {
  const filteredArray = useMemo(() => {
    if (category === 0) {
      return sortedArray;
    }
    return sortedArray.filter((item) => item.category === category);
  }, [array, category, sort]);
  return useSearch(filteredArray, searchValue);
}
