import React from "react";
import { useAxios } from "../hooks/useAxios";
import PizzaService from "../API/PizzaService";
import { useParams } from "react-router-dom";

export default function PizzaPage() {
  const { id } = useParams();
  const [item, setItem] = React.useState({});
  const [axiosPizza, isLoadingPizza, errorPizza] = useAxios(async (id) => {
    const response = await PizzaService.getItem(id);
    setItem(response.data);
  });

  React.useEffect(() => {
    axiosPizza(id);
  }, []);

  return (
    <div className="container">
      {errorPizza ? (
        <h2>–ü–∏—Ü—Ü–∞ –Ω–µ –∑–∞–≥—Ä—É–∑–∏–ª–∞—Å—å üòÖ</h2>
      ) : isLoadingPizza ? (
        <h2>–ó–∞–≥—Ä—É–∑–∫–∞ –ø–∏—Ü—Ü—ã üòÅ</h2>
      ) : (
        <>
          <img src={item.imageUrl} />
          <h2>
            {id} || {item.title}
          </h2>
          <p>–°—Ç–æ–∏–º–æ—Å—Ç—å: {item.price}</p>
        </>
      )}
    </div>
  );
}
