import React from "react";
import Categories from "../components/Categories";
import Sort, { selects } from "../components/Sort";
import { useAxios } from "../hooks/useAxios";
import PizzaService from "../API/PizzaService";
import PizzaCard from "../components/PizzaCard/PizzaCard";
import PizzaSkeletonCard from "../components/PizzaCard/PizzaSkeletonCard";
import { PizzaContext } from "../Contexts/PizzaContext";
import { useCategory } from "../hooks/usePizzas";
import Pagination from "../components/Pagination";
import { SearchContext } from "../Contexts/SearchContext";
import { useSelector, useDispatch } from "react-redux";
import { useNavigate, useSearchParams } from "react-router-dom";
import { setParams } from "../Redux/slices/filterSlice";
import { fetchPizzas } from "../Redux/slices/pizzasSlice";
import qs from "qs";
import { setItems } from "../Redux/slices/pizzasSlice";

export default function Home() {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  // const isSearchParams = React.useRef(false);
  const isMounted = React.useRef(false);
  let [searchParams, setSearchParams] = useSearchParams();
  // const [pizzas, setPizzas] = React.useState([]);
  const { items, status } = useSelector((state) => state.pizzas);

  const { filter, sort, currentPage } = useSelector((state) => state.filter);
  const { searchValue } = React.useContext(SearchContext);
  const [type, setType] = React.useState("number");

  // me
  const [axiosPizzas, isLoadingPizzas, errorPizzas] = useAxios(async (page) => {
    // const response = await PizzaService.getAll(page);
    dispatch(fetchPizzas(page));
    // setPizzas(response.data);
  });
  const sortedArr = useCategory([...items], sort, type, filter, searchValue);

  React.useEffect(() => {
    if (searchParams.size > 0) {
      const sort = selects.find((obj) => obj.sort === searchParams.get("sort"));

      dispatch(
        setParams({
          filter: searchParams.get("filter"),
          currentPage: searchParams.get("currentPage"),
          sort,
        })
      );
    }
  }, []);

  React.useEffect(() => {
    window.scrollTo(0, 0);
    axiosPizzas(currentPage);
  }, [currentPage]);

  React.useEffect(() => {
    const queryString = qs.stringify({
      sort: sort.sort,
      filter,
      currentPage,
    });
    navigate(`?${queryString}`);
  }, [sort.sort, filter, currentPage]);

  return (
    <div className="container">
      <div className="content__top">
        <PizzaContext.Provider value={{ setType, type }}>
          <Categories />
          <Sort />
        </PizzaContext.Provider>
      </div>
      <h2 className="content__title">–í—Å–µ –ø–∏—Ü—Ü—ã</h2>
      <div className="content__items">
        {status === "error" ? (
          <div>
            <h1>–ü—Ä–æ–∏–∑–æ—à–ª–∞ –æ—à–∏–±–∫–∞ üò•</h1>
          </div>
        ) : status === "loading" ? (
          [...Array(8)].map((_, index) => <PizzaSkeletonCard key={index} />)
        ) : (
          sortedArr.map((item) => <PizzaCard key={item.id} item={item} />)
        )}
      </div>
      }
      <Pagination />
    </div>
  );
}
