import { useState } from "react";

export function useAxios(callback) {
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState("");

  async function fetch() {
    try {
      setIsLoading(true);
      await callback();
    } catch (err) {
      setError(err.message);
    } finally {
      setIsLoading(false);
    }
  }
  return [fetch, isLoading, error];
}
